<script>
$(document).ready(function(){
    $("#booking_occupancy").change(function(){
		
		var booking_val=this.value;
		
		if(booking_val!=''){
			var booking_title=$('#booking_occupancy option:selected').text();
			
			var discount_code_ff=$("#discount-code").val();
			
			if(discount_code_ff!='' && discount_code_ff=='GS'){
				var discount_amount_val=500;
			}else {
				var discount_amount_val=250;
			}
			
			var final_amount=(booking_val-discount_amount_val);
			
			$('#discount_amount_id').val(discount_amount_val);
			$('#final_amount_id').val(final_amount);
			$('#booking_occupancy_title').val(booking_title);
			
			var final_amount		='$'+final_amount;
			var discount_amount_val	='$'+discount_amount_val;
			
			$('#discount_html').html('<div class="chalf-label">Early Bird Discount</div><div class="chalf-text" style="padding-top: 10px;"><span >'+discount_amount_val+'</span></div><div class="clear"></div>');
